#!/bin/bash

# Set KAFKA specific environment variables here.

# The java implementation to use.
export JAVA_HOME={{java64_home}}
export PATH=$PATH:$JAVA_HOME/bin
export PID_DIR={{kafka_pid_dir}}
export LOG_DIR={{kafka_log_dir}}
{% if kerberos_security_enabled or kafka_other_sasl_enabled %}
export KAFKA_KERBEROS_PARAMS="-Djavax.security.auth.useSubjectCredsOnly=false {{kafka_kerberos_params}}"
{% else %}
export KAFKA_KERBEROS_PARAMS={{kafka_kerberos_params}}
{% endif %}
# Add kafka sink to classpath and related depenencies
if [ -e "/usr/lib/ambari-metrics-kafka-sink/ambari-metrics-kafka-sink.jar" ]; then
  export CLASSPATH=$CLASSPATH:/usr/lib/ambari-metrics-kafka-sink/ambari-metrics-kafka-sink.jar
  export CLASSPATH=$CLASSPATH:/usr/lib/ambari-metrics-kafka-sink/lib/*
fi
if [ -f /etc/kafka/conf/kafka-ranger-env.sh ]; then
. /etc/kafka/conf/kafka-ranger-env.sh
fi

{% if kerberos_security_enabled %}
KafkaClient {
com.sun.security.auth.module.Krb5LoginModule required
useTicketCache=true
renewTicket=true
serviceName="{{kafka_bare_jaas_principal}}";
};
{% endif %}

/**
* Example of SASL/PLAIN Configuration
*
* KafkaServer {
*   org.apache.kafka.common.security.plain.PlainLoginModule required
*   username="admin"
*   password="admin-secret"
*   user_admin="admin-secret"
*   user_alice="alice-secret";
*   };
*
* Example of SASL/SCRAM
*
* KafkaServer {
*   org.apache.kafka.common.security.scram.ScramLoginModule required
*   username="admin"
*   password="admin-secret"
*   };
*
* Example of Enabling multiple SASL mechanisms in a broker:
*
*   KafkaServer {
*
*    com.sun.security.auth.module.Krb5LoginModule required
*    useKeyTab=true
*    storeKey=true
*    keyTab="/etc/security/keytabs/kafka_server.keytab"
*    principal="kafka/kafka1.hostname.com@EXAMPLE.COM";
*
*    org.apache.kafka.common.security.plain.PlainLoginModule required
*    username="admin"
*    password="admin-secret"
*    user_admin="admin-secret"
*    user_alice="alice-secret";
*
*    org.apache.kafka.common.security.scram.ScramLoginModule required
*    username="scram-admin"
*    password="scram-admin-secret";
*    };
*
**/

{% if kerberos_security_enabled %}

KafkaServer {
com.sun.security.auth.module.Krb5LoginModule required
useKeyTab=true
keyTab="{{kafka_keytab_path}}"
storeKey=true
useTicketCache=false
serviceName="{{kafka_bare_jaas_principal}}"
principal="{{kafka_jaas_principal}}";
};
KafkaClient {
com.sun.security.auth.module.Krb5LoginModule required
useTicketCache=true
renewTicket=true
serviceName="{{kafka_bare_jaas_principal}}";
};
Client {
com.sun.security.auth.module.Krb5LoginModule required
useKeyTab=true
keyTab="{{kafka_keytab_path}}"
storeKey=true
useTicketCache=false
serviceName="zookeeper"
principal="{{kafka_jaas_principal}}";
};
com.sun.security.jgss.krb5.initiate {
com.sun.security.auth.module.Krb5LoginModule required
renewTGT=false
doNotPrompt=true
useKeyTab=true
keyTab="{{kafka_keytab_path}}"
storeKey=true
useTicketCache=false
serviceName="{{kafka_bare_jaas_principal}}"
principal="{{kafka_jaas_principal}}";
};

{% endif %}

NiFiClient {
    com.sun.security.auth.module.Krb5LoginModule required
    useKeyTab=true
    keyTab="{{nifi_service_keytab}}"
    storeKey=true
    useTicketCache=false
    principal="{{nifi_service_principal}}";
};
RegistryClient {
    com.sun.security.auth.module.Krb5LoginModule required
    useKeyTab=true
    keyTab="{{nifi_service_keytab}}"
    storeKey=true
    useTicketCache=false
    principal="{{nifi_service_principal}}";
};

{
  "input":[
    {
      "type":"nifi_app",
      "rowtype":"service",
      "path":"{{nifi_log_dir}}/nifi-app.log"
    },
    {
      "type":"nifi_bootstrap",
      "rowtype":"service",
      "path":"{{nifi_log_dir}}/nifi-bootstrap.log"
    },
    {
      "type":"nifi_setup",
      "rowtype":"service",
      "path":"{{nifi_log_dir}}/nifi-setup.log"
    },
    {
      "type":"nifi_user",
      "rowtype":"service",
      "path":"{{nifi_log_dir}}/nifi-user.log"
    },
    {
      "type":"nifi_ca",
      "rowtype":"service",
      "path":"{{nifi_log_dir}}/nifi-ca.stdout"
    }
  ],
  "filter":[
    {
      "filter":"grok",
      "conditions":{
        "fields":{
          "type":[
            "nifi_app",
            "nifi_bootstrap",
            "nifi_setup",
            "nifi_user",
            "nifi_ca"
          ]
        }
      },
      "log4j_format":"",
      "multiline_pattern":"^(%{TIMESTAMP_ISO8601:logtime})",
      "message_pattern":"(?m)^%{TIMESTAMP_ISO8601:logtime}%{SPACE}%{LOGLEVEL:level}%{SPACE}\\[%{DATA:thread_name}\\]%{SPACE}%{GREEDYDATA:log_message}",
      "post_map_values":{
        "logtime":{
          "map_date":{
            "target_date_pattern":"yyyy-MM-dd HH:mm:ss,SSS"
          }
        }
      }
    }
  ]
}
